import React, { Component } from 'react';
import { useState } from 'react';
import { send } from 'emailjs-com';
import SendBtn from './SendBtn';
import CancelBtn from './CancelBtn';
import '../index.css';

class Contact extends Component {
    constructor(props) {
        super(props);
        this.state = {value: ''};

        this.handleInputChange = this.handleInputChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleInputChange(event) {
        const target = event.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;

        this.setState({
          [name]: value
        });
      }

    handleSubmit(event) {
        alert('A name was submitted: ' + this.state.value);
        event.preventDefault();
    }

    render () {
        return <div class="box" id="contactMe">
            <h3 class="title is-3">Contact Me</h3>
            <form onSubmit={this.handleSubmit}>
                <div class="field">
                    <label class="label">Name</label>
                    <div class="control">
                    <input class="input" type="text" name="name" placeholder="e.g. Johnny Appleseed" value={this.state.name} onChange={this.handleInputChange} />
                    </div>
                </div>

                <div class="field">
                    <label class="label">Email</label>
                    <div class="control">
                        <input class="input" type="email" name="email" placeholder="e.g. averycoolperson@email.com" value={this.state.email} onChange={this.handleInputChange} />
                    </div>
                </div>

                <div class="field">
                    <label class="label">Subject</label>
                    <div class="control">
                    <input class="input" type="text" name="subject" placeholder="e.g. I would like to offer you a million dollars!" value={this.state.subject} onChange={this.handleInputChange} />
                    </div>
                </div>

                <div class="field">
                    <label class="label">Message</label>
                    <div class="control">
                        <textarea class="textarea" name="message" placeholder="" value={this.state.message} onChange={this.handleInputChange} />
                    </div>
                </div>

                <div class="buttons is-grouped is-justify-content-end">
                    <SendBtn />
                    <CancelBtn />
                </div>
            </form>
        </div>
    }
}

export default Contact;
