import React, { Component } from 'react';
import '../index.css';

class TextBox extends Component {
    render () {
        return <div class="field">
            <label class="label">Message</label>
            <div class="control">
                <textarea class="textarea" name="message" placeholder="" value={this.state.message} onChange={this.handleInputChange} />
            </div>
        </div>
    }
}

export default TextBox;
