import React, { Component, useState } from 'react';
import * as emailjs from 'emailjs-com';
import SendBtn from './SendBtn';
import CancelBtn from './CancelBtn';
import '../index.css';

class Contact extends Component {
    state = {
        name: '',
        email: '',
        subject: '',
        message: ''
    }

    handleChange = (param, event) => {
        this.setState({ [param]: event.target.value })
    }

    handleSubmit(event) {
        event.preventDefault();

        const { name, email, subject, message } = this.state
        let templateParams = {
            name: name,
            email: email,
            to_name: 'Sean',
            subject: subject,
            message: message,
        }

        emailjs.send(
            'my_dev_gmail',
            'template_j6ztv0i',
            templateParams,
            'user_e2SBprNH4w0Ma1Ze0RS4r'
        )

        alert("Your message has been sent!");

        this.resetForm();
    }

    resetForm() {
        this.setState({
            name: '',
            email: '',
            subject: '',
            message: '',
        })
    }

    cancelForm = () => {
        document.getElementById("contactForm")
    }

    render () {
        return <div>
                <div class="box" id="contactMe">
                <h3 class="title is-3">Contact Me</h3>
                <form id="contactForm" onSubmit={this.handleSubmit.bind(this)}>
                    <div class="field">
                        <label class="label">Name</label>
                        <div class="control">
                        <input class="input" type="text" name="name" placeholder="e.g. Johnny Appleseed" value={this.state.name} onChange={this.handleChange.bind(this, 'name')} />
                        </div>
                    </div>

                    <div class="field">
                        <label class="label">Email</label>
                        <div class="control">
                            <input class="input" type="email" name="email" placeholder="e.g. averycoolperson@email.com" value={this.state.email} onChange={this.handleChange.bind(this, 'email')} />
                        </div>
                    </div>

                    <div class="field">
                        <label class="label">Subject</label>
                        <div class="control">
                        <input class="input" type="text" name="subject" placeholder="e.g. I would like to offer you a million dollars!" value={this.state.subject} onChange={this.handleChange.bind(this, 'subject')} />
                        </div>
                    </div>

                    <div class="field">
                        <label class="label">Message</label>
                        <div class="control">
                            <textarea class="textarea" name="message" placeholder="" value={this.state.message} onChange={this.handleChange.bind(this, 'message')} />
                        </div>
                    </div>

                    <div class="buttons is-grouped is-justify-content-end">
                        <SendBtn />
                        <CancelBtn onClick={this.reset()} />
                    </div>
                </form>
            </div>

            <div id="emailSent" class="modal">
            <div class="modal-background"></div>
            <div class="modal-content">
                <div class="box">
                    <h5 class="title is-5">Thank you! Your message has been sent!</h5>
                </div>
            </div>
                <button class="modal-close is-large" aria-label="close"></button>
            </div>
        </div>
    }
}

export default Contact;
