import React, { Component } from 'react';
import * as emailjs from 'emailjs-com';
import SendBtn from './SendBtn';
import CancelBtn from './CancelBtn';
import '../index.css';

class Contact extends Component {
    state = {
        name: '',
        email: '',
        subject: '',
        message: ''
    }

    constructor(props) {
        super(props);
        this.state = {value: ''};

        this.handleChange = this.handleChange.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
    }

    handleChange(event) {
        const target = event.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;

        this.setState({
          [name]: value
        });
      }

    onSubmit(event) {
        event.preventDefault();

        const { name, email, subject, message } = this.state
        let templateParams = {
            from_name: email,
            to_name: '<YOUR_EMAIL_ID>',
            subject: subject,
            message_html: message,
        }
        emailjs.send(
            'gmail',
            'template_XXXXXXXX',
            templateParams,
            'user_XXXXXXXXXXXXXXXXXXXX'
        )
        this.resetForm()
    }

    render () {
        return <div class="box" id="contactMe">
            <h3 class="title is-3">Contact Me</h3>
            <form onSubmit={this.onSubmit}>
                <div class="field">
                    <label class="label">Name</label>
                    <div class="control">
                    <input class="input" type="text" name="from_name" placeholder="e.g. Johnny Appleseed" value={this.state.name} onChange={this.handleChange} />
                    </div>
                </div>

                <div class="field">
                    <label class="label">Email</label>
                    <div class="control">
                        <input class="input" type="email" name="from_email" placeholder="e.g. averycoolperson@email.com" value={this.state.email} onChange={this.handleChange} />
                    </div>
                </div>

                <div class="field">
                    <label class="label">Subject</label>
                    <div class="control">
                    <input class="input" type="text" name="subject" placeholder="e.g. I would like to offer you a million dollars!" value={this.state.subject} onChange={this.handleChange} />
                    </div>
                </div>

                <div class="field">
                    <label class="label">Message</label>
                    <div class="control">
                        <textarea class="textarea" name="message" placeholder="" value={this.state.message} onChange={this.handleChange} />
                    </div>
                </div>

                <div class="buttons is-grouped is-justify-content-end">
                    <SendBtn />
                    <CancelBtn />
                </div>
            </form>
        </div>
    }
}

export default Contact;
