import React, { Component } from 'react';
import * as emailjs from 'emailjs-com';
import SendBtn from './SendBtn';
import CancelBtn from './CancelBtn';
import '../index.css';

class Contact extends Component {
    state = {
        name: '',
        email: '',
        subject: '',
        message: ''
    }

    handleChange = (param, event) => {
        this.setState({ [param]: event.target.value })
    }

    onSubmit(event) {
        event.preventDefault();

        const { name, email, subject, message } = this.state
        let templateParams = {
            from_name: email,
            to_name: 'service_zjj8ucv',
            subject: subject,
            message_html: message,
        }

        emailjs.send(
            'gmail',
            'template_j6ztv0i',
            templateParams,
            'user_e2SBprNH4w0Ma1Ze0RS4r'
        )

        this.resetForm()
    }

    resetForm() {
        this.setState({
            name: '',
            email: '',
            subject: '',
            message: '',
        })
    }

    render () {
        return <div class="box" id="contactMe">
            <h3 class="title is-3">Contact Me</h3>
            <form onSubmit={this.onSubmit}>
                <div class="field">
                    <label class="label">Name</label>
                    <div class="control">
                    <input class="input" type="text" name="name" placeholder="e.g. Johnny Appleseed" value={this.state.name} onChange={this.handleChange} />
                    </div>
                </div>

                <div class="field">
                    <label class="label">Email</label>
                    <div class="control">
                        <input class="input" type="email" name="email" placeholder="e.g. averycoolperson@email.com" value={this.state.email} onChange={this.handleChange} />
                    </div>
                </div>

                <div class="field">
                    <label class="label">Subject</label>
                    <div class="control">
                    <input class="input" type="text" name="subject" placeholder="e.g. I would like to offer you a million dollars!" value={this.state.subject} onChange={this.handleChange} />
                    </div>
                </div>

                <div class="field">
                    <label class="label">Message</label>
                    <div class="control">
                        <textarea class="textarea" name="message" placeholder="" value={this.state.message} onChange={this.handleChange} />
                    </div>
                </div>

                <div class="buttons is-grouped is-justify-content-end">
                    <SendBtn />
                    <CancelBtn />
                </div>
            </form>
        </div>
    }
}

export default Contact;
