import React, { Component } from 'react';
import '../index.css';

class InputEmail extends Component {
    render () {
        return <div class="field">
            <label class="label">Email</label>
            <div class="control">
                <input class="input" type="email" name="email" placeholder="e.g. averycoolperson@email.com" value={this.state.email} onChange={this.handleInputChange} />
            </div>
        </div>
    }
}

export default InputEmail;
